import React, { useState } from 'react';
import { useCart } from '../../context/CartContext';

const ShippingForm: React.FC = () => {
    const { setShippingAddress } = useCart();
    const [address, setAddress] = useState({
        firstName: '',
        lastName: '',
        street: '',
        city: '',
        postalCode: '',
        country: '',
        telephone: ''
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setAddress({
            ...address,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        await setShippingAddress(address);
        // Przejdź do formularza płatności
    };

    return (
        <form className="shipping-form" onSubmit={handleSubmit}>
            <h3>Shipping Address</h3>
            <input type="text" name="firstName" placeholder="First Name" onChange={handleChange} required />
            <input type="text" name="lastName" placeholder="Last Name" onChange={handleChange} required />
            <input type="text" name="street" placeholder="Street Address" onChange={handleChange} required />
            <input type="text" name="city" placeholder="City" onChange={handleChange} required />
            <input type="text" name="postalCode" placeholder="Postal Code" onChange={handleChange} required />
            <input type="text" name="country" placeholder="Country" onChange={handleChange} required />
            <input type="text" name="telephone" placeholder="Telephone" onChange={handleChange} required />
            <button type="submit">Proceed to Payment</button>
        </form>
    );
};

export default ShippingForm;
