import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './ProductDetail.scss';

interface Product {
    id: number;
    name: string;
    price: {
        regularPrice: {
            amount: {
                value: number;
                currency: string;
            };
        };
    };
    small_image?: {
        url?: string;
        label?: string;
    };
    short_description?: {
        html?: string;
    };
}

const ProductDetail: React.FC = () => {
    const { sku } = useParams<{ sku: string }>();
    const [product, setProduct] = useState<Product | null>(null);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchProductDetails = async () => {
            const query = `
                {
                    products(
                        filter: {
                            sku: {
                                eq: "${sku}"
                            }
                        },
                        pageSize: 1,
                        currentPage: 1
                    ) {
                        items {
                            id
                            name
                            short_description {
                                html
                            }
                            price {
                                regularPrice {
                                    amount {
                                        value
                                        currency
                                    }
                                }
                            }
                            small_image {
                                url
                                label
                            }
                        }
                    }
                }
            `;

            try {
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ query }),
                });

                if (!response.ok) {
                    throw new Error('Failed to fetch product details');
                }

                const data = await response.json();
                setProduct(data.data.products.items[0]);
            } catch (err) {
                if (err instanceof Error) {
                    setError(err.message);
                } else {
                    setError('An unknown error occurred');
                }
            } finally {
                setLoading(false);
            }
        };

        if (sku) {
            fetchProductDetails();
        }
    }, [sku]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="product-detail">
            {product && (
                <div className="product-detail-container">
                    <div className="product-image">
                        {product.small_image && product.small_image.url ? (
                            <img src={product.small_image.url} alt={product.small_image.label} />
                        ) : (
                            <p>No image available</p>
                        )}
                    </div>
                    <div className="product-info">
                        <h1>{product.name}</h1>
                        <p className="product-price">
                            Price: {product.price.regularPrice.amount.value} {product.price.regularPrice.amount.currency}
                        </p>
                        {product.short_description && (
                            <div
                                className="short-description"
                                dangerouslySetInnerHTML={{ __html: product.short_description.html || '' }}
                            />
                        )}
                        <button className="add-to-cart">Add to Cart</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ProductDetail;
