import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './UserDashboard.scss';
import UserSidebar from '../../components/UserSidebar/UserSidebar';

const UserDashboard: React.FC = () => {
    const [userName, setUserName] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserData = async () => {
            const token = sessionStorage.getItem('auth_token');
            if (!token) return;

            const query = `
                query {
                    customer {
                        firstname
                        lastname
                    }
                }
            `;

            try {
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`,
                    },
                    body: JSON.stringify({ query }),
                });

                const result = await response.json();
                if (result.data && result.data.customer) {
                    setUserName(result.data.customer.firstname);
                }
            } catch (err) {
                console.error("Error fetching user data:", err);
            }
        };

        fetchUserData();
    }, []);

    const handleLogout = () => {
        sessionStorage.removeItem('auth_token');
        sessionStorage.removeItem('user_name');
        navigate('/login');
    };

    return (
        <div className="user-dashboard">
            <UserSidebar/>

            <div className="user-dashboard__content">
                <h2>Welcome, {userName}</h2>
                <section>
                    <h3>Account Information</h3>
                    <div className="info-section">
                        <div className="contact-info">
                            <h4>Contact Information</h4>
                            <div></div>
                            <a href="#">Edit</a> | <a href="#">Change Password</a>
                        </div>
                        <div className="newsletter">
                            <h4>Newsletters</h4>
                            <div></div>
                            <a href="#">Edit</a>
                        </div>
                    </div>

                    <h3>Address Book</h3>
                    <div className="info-section">
                        <div>
                            <h4>Default Billing Address</h4>
                            <div></div>
                            <a href="#">Edit Address</a>
                        </div>
                        <div>
                            <h4>Default Shipping Address</h4>
                            <div></div>
                            <a href="#">Edit Address</a>
                        </div>
                    </div>
                </section>
                <button onClick={handleLogout} className="logout-button">
                    Logout
                </button>
            </div>
        </div>
    );
};

export default UserDashboard;
