import React, { useState } from 'react';
import { useCart } from '../../context/CartContext';

const PaymentPage: React.FC = () => {
    const { items, total, clearCart, setPaymentMethod, placeOrder } = useCart();
    const [paymentMethod, setPaymentMethodState] = useState("checkmo");
    const [error, setError] = useState<string | null>(null);
    const [isLoading, setIsLoading] = useState(false);
    const [isOrderPlaced, setIsOrderPlaced] = useState(false);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setIsLoading(true);
        setError(null);

        try {
            await setPaymentMethod(paymentMethod);
            const orderNumber = await placeOrder();
            console.log("Zamówienie złożone pomyślnie. Numer zamówienia:", orderNumber);
            setIsOrderPlaced(true);
            clearCart();
        } catch (error) {
            console.error("Błąd podczas składania zamówienia:", error);
            setError("Wystąpił błąd podczas składania zamówienia. Proszę spróbować ponownie.");
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="payment-page">
            <h2>Podsumowanie zamówienia</h2>
            {items.length > 0 ? (
                <>
                    <div className="order-summary">
                        {items.map((item) => (
                            <div key={item.id} className="order-item">
                                <p>{item.name}</p>
                                <p>Cena: {item.price} PLN</p>
                                <p>Ilość: {item.quantity}</p>
                            </div>
                        ))}
                        <h3>Łącznie: {total} PLN</h3>
                    </div>
                    <form className="payment-form" onSubmit={handleSubmit}>
                        <h3>Metoda płatności</h3>
                        <select
                            name="paymentMethod"
                            onChange={(e) => setPaymentMethodState(e.target.value)}
                            value={paymentMethod}
                        >
                            <option value="checkmo">Check / Money Order</option>
                        </select>
                        <button type="submit" disabled={isLoading}>Złóż zamówienie</button>
                        {isLoading && <p>Trwa składanie zamówienia...</p>}
                        {error && <p style={{ color: 'red' }}>{error}</p>}
                    </form>
                </>
            ) : (
                <p>Koszyk jest pusty. Dodaj produkty, aby kontynuować.</p>
            )}
            {isOrderPlaced && <h3>Dziękujemy za zakupy!</h3>}
        </div>
    );
};

export default PaymentPage;
