import React, { useEffect, useState } from 'react';
import Slider from 'react-slick';
import './Recommendation.scss';

interface Product {
    id: number;
    name: string;
    image: string;
    price: number;
}

const Recommendation: React.FC = () => {
    const [products, setProducts] = useState<Product[]>([]);

    useEffect(() => {
        const fetchData = async () => {
            const response = await fetch('/api/products'); // Upewnij siƒô, ≈ºe to jest prawid≈Çowy endpoint
            const data = await response.json();
            setProducts(data);
        };

        fetchData();
    }, []);

    const settings = {
        dots: true, // W≈ÇƒÖcz kropki
        infinite: true,
        speed: 500,
        slidesToShow: 5, // Wy≈õwietl 5 kart
        slidesToScroll: 1,
        centerMode: true, // Wy≈õrodkowanie
        centerPadding: '0px', // Ustaw padding na 0
    };

    return (
        <div className="recommendationSection">
            <h1>Recommendations</h1>
            <div className="carousel-container">
                <Slider {...settings}>
                    {products.map(product => (
                        <div key={product.id} className="product-card">
                            <img src={product.image} alt={product.name} />
                            <h2>{product.name}</h2>
                            <p>{product.price} PLN</p>
                            <button className="add-to-cart-button">Add to Cart</button>
                            <div className="product-icons">
                                <span className="icon-heart">‚ù§Ô∏è</span>
                                <span className="icon-loupe">üîç</span>
                            </div>
                        </div>
                    ))}
                </Slider>
            </div>
        </div>
    );
};

export default Recommendation;
