import React, { useEffect, useState } from 'react';
import { CarouselProvider, Carousel, Slide, ButtonBack, ButtonNext } from 'nuka-carousel'; // Użyj CarouselProvider i Slide
import './Recommendation.scss';

interface Product {
    id: number;
    name: string;
    price?: {
        regularPrice?: {
            amount?: {
                value?: number;
                currency?: string;
            };
        };
    };
    small_image?: {
        url?: string;
        label?: string;
    };
}

const Recommendation: React.FC = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);
    const [favorites, setFavorites] = useState<number[]>([]);

    const toggleFavorite = (id: number) => {
        if (favorites.includes(id)) {
            setFavorites(favorites.filter(favId => favId !== id));
        } else {
            setFavorites([...favorites, id]);
        }
    };

    useEffect(() => {
        const fetchProducts = async () => {
            const query = `
                {
                    products(search: "") {
                        items {
                            id
                            name
                            price {
                                regularPrice {
                                    amount {
                                        value
                                        currency
                                    }
                                }
                            }
                            small_image {
                                url
                                label
                            }
                        }
                    }
                }
            `;

            try {
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ query }),
                });

                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }

                const data = await response.json();
                setProducts(data.data.products.items);
            } catch (err: unknown) {
                console.error('Fetch error:', err);
                if (err instanceof Error) {
                    setError(err.message);
                } else {
                    setError('An unknown error occurred');
                }
            } finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="recommendationSection">
            <h1>Recommendations</h1>
            <CarouselProvider
                naturalSlideWidth={100} // Szerokość naturalna slajdu
                naturalSlideHeight={125} // Wysokość naturalna slajdu
                totalSlides={products.length} // Łączna liczba slajdów
                visibleSlides={4} // Liczba widocznych slajdów
                step={1} // Liczba przesuwanych slajdów
                isIntrinsicHeight={true} // Ustal wysokość na podstawie zawartości
                className="carousel-container"
            >
                <Carousel>
                    {products.map((product, index) => (
                        <Slide key={product.id} index={index}>
                            <div className="product-card">
                                {product.small_image && product.small_image.url ? (
                                    <img src={product.small_image.url} alt={product.small_image.label} className="product-image" />
                                ) : (
                                    <p>No image available</p>
                                )}
                                <h2>{product.name}</h2>
                                {product.price && product.price.regularPrice ? (
                                    <p>
                                        Price: {product.price.regularPrice.amount?.value} {product.price.regularPrice.amount?.currency}
                                    </p>
                                ) : (
                                    <p>Price not available</p>
                                )}
                                <button className="add-to-cart-button">Add To Cart</button>
                                <div className="product-icons">
                                    <span
                                        className={`icon-heart ${favorites.includes(product.id) ? 'active' : ''}`}
                                        onClick={() => toggleFavorite(product.id)}
                                    >
                                        ♥
                                    </span>
                                    <i className="fas fa-search icon-loupe"></i>
                                </div>
                            </div>
                        </Slide>
                    ))}
                </Carousel>
                <ButtonBack className="carousel-arrow">❮</ButtonBack>
                <ButtonNext className="carousel-arrow">❯</ButtonNext>
            </CarouselProvider>
        </div>
    );
};

export default Recommendation;
