import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useCart } from '../../context/CartContext';
import WishlistButton from '../../components/WishlistButton/WishlistButton';
import './ProductDetail.scss';
import { useTranslation } from 'react-i18next';

interface Category {
    id: number;
    name: string;
    url_path: string;
    parent?: Category;
}

interface Product {
    id: number;
    name: string;
    sku: string;
    price: {
        regularPrice: {
            amount: {
                value: number;
                currency: string;
            };
        };
    };
    stock_status?: string;
    small_image?: {
        url?: string;
        label?: string;
    };
    short_description?: {
        html?: string;
    };
    categories?: Array<Category>;
}

const ProductDetail: React.FC = () => {
    const { sku } = useParams<{ sku: string }>();
    const [product, setProduct] = useState<Product | null>(null);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);
    const [wishlistId, setWishlistId] = useState<number | null>(null);
    const [quantity, setQuantity] = useState<number>(1);
    const [favorites, setFavorites] = useState<number[]>([]);
    const { t } = useTranslation();
    const { addItem } = useCart();

    useEffect(() => {
        const fetchProductDetails = async () => {
            const query = `
                {
                    products(
                        filter: {
                            sku: {
                                eq: "${sku}"
                            }
                        },
                        pageSize: 1,
                        currentPage: 1
                    ) {
                        items {
                            id
                            sku
                            name
                            stock_status
                            short_description {
                                html
                            }
                            price {
                                regularPrice {
                                    amount {
                                        value
                                        currency
                                    }
                                }
                            }
                            small_image {
                                url
                                label
                            }
                            categories {
                                id
                                name
                                url_path
                                parent {
                                    id
                                    name
                                    url_path
                                    parent {
                                        id
                                        name
                                        url_path
                                    }
                                }
                            }
                        }
                    }
                    customer {
                        wishlist {
                            id
                        }
                    }
                }
            `;

            try {
                const token = sessionStorage.getItem('auth_token');
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        ...(token ? { 'Authorization': `Bearer ${token}` } : {}),
                    },
                    body: JSON.stringify({ query }),
                });

                if (!response.ok) {
                    throw new Error('Failed to fetch product details');
                }

                const data = await response.json();
                const fetchedProduct = data.data.products.items[0];
                setProduct(fetchedProduct);

                if (data.data.customer && data.data.customer.wishlist) {
                    setWishlistId(data.data.customer.wishlist.id);
                }
            } catch (err) {
                setError(err instanceof Error ? err.message : 'An unknown error occurred');
            } finally {
                setLoading(false);
            }
        };

        if (sku) {
            fetchProductDetails();
        }
    }, [sku]);

    const handleQuantityChange = (change: number) => {
        if (product?.stock_status === 'OUT_OF_STOCK') return;
        const maxQuantity = product ? 10 : 1;
        setQuantity(prev => Math.max(1, Math.min(prev + change, maxQuantity)));
    };

    const addToCart = () => {
        if (product) {
            addItem(product.sku, quantity);
            alert(`Added ${quantity} x ${product.name} to cart!`);
        }
    };

    const toggleFavorite = (productId: number) => {
        if (favorites.includes(productId)) {
            setFavorites(favorites.filter(favId => favId !== productId));
        } else {
            setFavorites([...favorites, productId]);
        }
    };

    const buildBreadcrumbs = () => {
        const categoryPath: Array<Category> = [];

        // Przechodzimy przez hierarchię kategorii, aby utworzyć ścieżkę breadcrumbs
        let currentCategory = product?.categories ? product.categories[0] : null;
        while (currentCategory) {
            categoryPath.unshift(currentCategory);
            currentCategory = currentCategory.parent || null;
        }

        return (
            <>
                <Link to="/">Home</Link>
                {categoryPath.map((category, index) => (
                    <React.Fragment key={category.id}>
                        &gt; <Link to={`/${category.url_path}`}>{category.name}</Link>
                    </React.Fragment>
                ))}
                &gt; {product?.name}
            </>
        );
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="product-detail">
            <div className="breadcrumb">
                {buildBreadcrumbs()}
            </div>

            {product && (
                <div className="product-detail-container">
                    <div className="product-image">
                        {product.small_image && product.small_image.url ? (
                            <img src={product.small_image.url} alt={product.small_image.label} />
                        ) : (
                            <p>No image available</p>
                        )}
                    </div>
                    <div className="product-info">
                        <h1 className="product-title">{product.name}</h1>
                        <div className="price-stock-sku">
                            <span className="product-price">
                                ${product.price.regularPrice.amount.value.toFixed(2)}
                            </span>
                            <span
                                className={`product-stock ${product.stock_status === 'IN_STOCK' ? 'in-stock' : 'out-of-stock'}`}>
                                {product.stock_status === 'IN_STOCK' ? t('ProductDetail.IN_STOCK') : t('ProductDetail.OUT_OF_STOCK')}
                            </span>
                            <span className="product-sku">SKU: {product.sku}</span>
                        </div>
                        <div className="quantity">
                            <label>{t('ProductDetail.quantity')}: </label>
                            <button onClick={() => handleQuantityChange(-1)} disabled={quantity <= 1}>-</button>
                            <span>{quantity}</span>
                            <button onClick={() => handleQuantityChange(1)} disabled={quantity >= 10}>+</button>
                        </div>
                        <div className="actions">
                            <button className="add-to-cart" onClick={addToCart}>{t('ProductDetail.add_to_cart')}</button>
                            <WishlistButton
                                productId={product.id}
                                sku={product.sku}
                                wishlistId={wishlistId}
                                favorites={favorites}
                                toggleFavorite={toggleFavorite}
                            />
                        </div>
                        <div className="details">
                            <h2>{t('ProductDetail.details')}</h2>
                            <div
                                className="short-description"
                                dangerouslySetInnerHTML={{__html: product.short_description?.html || ''}}
                            />
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ProductDetail;
