import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../../context/CartContext';
const PaymentPage: React.FC = () => {
    const { items, total, shippingAddress, shippingMethod, setShippingMethod, placeOrder, clearCart } = useCart();
    const [paymentMethod, setPaymentMethodState] = useState("checkmo");
    const [error, setError] = useState<string | null>(null);
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const handlePaymentMethodChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setPaymentMethodState(e.target.value);
    };

    const handleShippingMethodChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
        setShippingMethod(e.target.value);
    };

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        setIsLoading(true);
        setError(null);

        try {
            if (shippingAddress && shippingMethod) { // Ensure address and shipping method are selected
                await placeOrder();
                clearCart();
                navigate(`/order/success`); // Redirect to order success page
            } else {
                setError("Adres dostawy i metoda wysyłki są wymagane.");
            }
        } catch (error) {
            setError("Wystąpił błąd podczas składania zamówienia.");
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div>
            <h2>Płatność</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <h3>Metoda wysyłki</h3>
                    <select onChange={handleShippingMethodChange} required>
                        <option value="">Wybierz metodę wysyłki</option>
                        <option value="standard">Standardowa</option>
                        <option value="express">Ekspresowa</option>
                    </select>
                </div>

                <div>
                    <h3>Metoda płatności</h3>
                    <label>
                        <input
                            type="radio"
                            value="checkmo"
                            checked={paymentMethod === 'checkmo'}
                            onChange={handlePaymentMethodChange}
                        />
                        Płatność przy odbiorze
                    </label>
                </div>

                {error && <div>{error}</div>}
                <button type="submit" disabled={isLoading}>Złóż zamówienie</button>
            </form>
        </div>
    );
};

export default PaymentPage;
