import React, { useEffect, useState } from 'react';
import { CarouselProvider, Carousel } from 'nuka-carousel';
import './Recommendation.scss';

interface Product {
    id: number;
    name: string;
    image: string;
    price: number;
}

const Recommendation: React.FC = () => {
    const [products, setProducts] = useState<Product[]>([]);

    useEffect(() => {
        // Przyk≈Çad pobierania danych (w rzeczywisto≈õci u≈ºyj swojego API)
        const fetchData = async () => {
            const response = await fetch('/api/products'); // Upewnij siƒô, ≈ºe to jest prawid≈Çowy endpoint
            const data = await response.json();
            setProducts(data);
        };

        fetchData();
    }, []);

    return (
        <div className="recommendationSection">
            <h1>Recommendations</h1>
            <div className="carousel-container">
                <CarouselProvider
                    naturalSlideWidth={100}
                    naturalSlideHeight={125}
                    totalSlides={products.length}
                    visibleSlides={4}
                    step={1}
                    isIntrinsicHeight={true}
                >
                    <Carousel
                        dragging={true}
                        wrapAround={true}
                        className="nuka-carousel"
                        renderCenterLeftControls={({ previousSlide }) => (
                            <button className="carousel-arrow" onClick={previousSlide}>‚ùÆ</button>
                        )}
                        renderCenterRightControls={({ nextSlide }) => (
                            <button className="carousel-arrow" onClick={nextSlide}>‚ùØ</button>
                        )}
                    >
                        {products.map(product => (
                            <div key={product.id} className="product-card">
                                <img src={product.image} alt={product.name} />
                                <h2>{product.name}</h2>
                                <p>{product.price} PLN</p>
                                <button className="add-to-cart-button">Add to Cart</button>
                                <div className="product-icons">
                                    <span className="icon-heart">‚ù§Ô∏è</span>
                                    <span className="icon-loupe">üîç</span>
                                </div>
                            </div>
                        ))}
                    </Carousel>
                </CarouselProvider>
            </div>
        </div>
    );
};

export default Recommendation;
