import React, { useEffect, useState } from 'react';
import UserSidebar from '../../components/UserSidebar/UserSidebar';
import './CustomerOrders.scss';

interface Order {
    order_number: string;
    id: string;
    created_at: string;
    grand_total: number;
    status: string;
}

const CustomerOrdersPage: React.FC = () => {
    const [orders, setOrders] = useState<Order[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchOrders = async () => {
            const token = sessionStorage.getItem('auth_token');
            if (!token) {
                setError("User is not logged in.");
                setLoading(false);
                return;
            }

            const query = `
                query {
                    customerOrders {
                        items {
                            order_number
                            id
                            created_at
                            grand_total
                            status
                        }
                    }
                }
            `;

            try {
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`,
                    },
                    body: JSON.stringify({ query }),
                });

                const result = await response.json();
                if (result.data && result.data.customerOrders) {
                    setOrders(result.data.customerOrders.items);
                } else {
                    setError("Failed to fetch orders.");
                }
            } catch (err) {
                setError("Error fetching orders: " + err);
            } finally {
                setLoading(false);
            }
        };

        fetchOrders();
    }, []);

    return (
        <div className="customer-orders-page">
            <UserSidebar />

            <div className="customer-orders-page__content">
                <h2>My Orders</h2>
                <section>
                    {loading && <p>Loading...</p>}
                    {error && <p>{error}</p>}

                    {orders.length > 0 ? (
                        <table>
                            <thead>
                            <tr>
                                <th>Order Number</th>
                                <th>Order ID</th>
                                <th>Created At</th>
                                <th>Grand Total</th>
                                <th>Status</th>
                            </tr>
                            </thead>
                            <tbody>
                            {orders.map((order) => (
                                <tr key={order.id}>
                                    <td>{order.order_number}</td>
                                    <td>{order.id}</td>
                                    <td>{new Date(order.created_at).toLocaleString()}</td>
                                    <td>${order.grand_total}</td>
                                    <td>{order.status}</td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
                    ) : (
                        <p>You have no orders.</p>
                    )}
                </section>
            </div>
        </div>
    );
};

export default CustomerOrdersPage;
