import React, { useState } from 'react';
import { useCart } from '../../context/CartContext';

const PaymentForm: React.FC = () => {
    const { setPaymentMethod, placeOrder } = useCart();
    const [paymentMethod, setPaymentMethodState] = useState("checkmo");

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        await setPaymentMethod(paymentMethod);
        await placeOrder();
    };

    return (
        <form className="payment-form" onSubmit={handleSubmit}>
            <h3>Payment Method</h3>
            <select name="paymentMethod" onChange={(e) => setPaymentMethodState(e.target.value)}>
                <option value="checkmo">Check / Money Order</option>
                {/* Możesz dodać inne metody płatności */}
            </select>
            <button type="submit">Place Order</button>
        </form>
    );
};

export default PaymentForm;
