import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import ShippingForm from './ShippingForm';
import PaymentForm from './PaymentForm';
import './Checkout.scss';

const Checkout: React.FC = () => {
    const [cartItems, setCartItems] = useState([]);
    const [shippingData, setShippingData] = useState(null);
    const [isShippingCompleted, setIsShippingCompleted] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        // Pobierz produkty z koszyka za pomocą GraphQL Magento
        const fetchCartItems = async () => {
            const response = await axios.post('/graphql', {
                query: `
                    query GetCartItems {
                        cart {
                            items {
                                id
                                product {
                                    name
                                    sku
                                    price {
                                        value
                                        currency
                                    }
                                }
                                quantity
                            }
                        }
                    }
                `
            });
            setCartItems(response.data.data.cart.items);
        };
        fetchCartItems();
    }, []);

    // Zapisz dane adresowe
    const handleShippingComplete = (data: any) => {
        setShippingData(data);
        setIsShippingCompleted(true);
    };

    // Jeśli użytkownik zakończył adresowanie, przechodzimy do wyboru metody płatności
    return (
        <div className="checkout">
            <h2>Checkout</h2>
            <div className="cart-summary">
                <h3>Cart Summary</h3>
                {cartItems.map((item, index) => (
                    <div key={index} className="cart-item">
                        <p>{item.product.name}</p>
                        <p>Quantity: {item.quantity}</p>
                        <p>Price: {item.product.price.value} {item.product.price.currency}</p>
                    </div>
                ))}
            </div>

            {!isShippingCompleted ? (
                <ShippingForm onComplete={handleShippingComplete} />
            ) : (
                <PaymentForm shippingData={shippingData} cartItems={cartItems} />
            )}
        </div>
    );
};

export default Checkout;
