// TopBullions.tsx
import React from 'react';
import { Link } from 'react-router-dom';
import './TopBullions.scss';
import { useTranslation } from 'react-i18next';


// Definicja typu dla poszczególnych elementów
interface Bullion {
    title: string;
    titleKey: string;  // Klucz tłumaczenia
    image: string;
    link: string;
}

const bullions: Bullion[] = [
    {
        title: 'Gold',
        titleKey: 'topBullions.gold',
        image: '/assets/images/gold.jpg',
        link: '/guides/gold'
    },
    {
        title: 'Silver',
        titleKey: 'topBullions.silver',
        image: '/assets/images/silver.jpg',
        link: '/guides/silver'
    },
    {
        title: 'Platinum',
        titleKey: 'topBullions.platinum',
        image: '/assets/images/platinum.jpg',
        link: '/guides/platinum'
    }
];

const TopBullions: React.FC = () => {
    const { t } = useTranslation();

    return (
        <section className="top-bullions">
            <h2>{t('topBullions.topBullions')}</h2>
            <div className="bullion-container">
            {bullions.map((bullion, index) => (
                    <div key={index} className="bullion-card">
                        <img src={bullion.image} alt={t(bullion.titleKey)} className="bullion-image" />
                        <h3>{t(bullion.titleKey)}</h3>
                        <Link to={bullion.link} className="learn-more">{t('topBullions.learnMore')}</Link>
                    </div>
                ))}
            </div>
        </section>
    );
};

export default TopBullions;
