import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useCart } from '../../context/CartContext';

const PaymentPage: React.FC = () => {
    const [paymentMethod, setPaymentMethod] = useState('Credit Card');
    const { items, placeOrder, clearCart } = useCart();
    const location = useLocation();
    const navigate = useNavigate();

    const shippingData = location.state?.shippingData;

    const handlePlaceOrder = async () => {
        if (!shippingData || items.length === 0) {
            alert('Shipping details or cart items are missing');
            return;
        }

        const orderNumber = await placeOrder();
        if (orderNumber) {
            alert(`Order placed successfully! Your order number is ${orderNumber}`);
            clearCart();
            navigate('/confirmation', { state: { orderNumber } });
        } else {
            alert('Failed to place order. Please try again.');
        }
    };

    return (
        <div className="payment-form">
            <h3>Select Payment Method</h3>
            <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                <option value="Credit Card">Credit Card</option>
                <option value="PayPal">PayPal</option>
                <option value="Bank Transfer">Bank Transfer</option>
            </select>
            <button onClick={handlePlaceOrder}>Place Order</button>
        </div>
    );
};

export default PaymentPage;
