// TopBullions.tsx
import React from 'react';
import { Link } from 'react-router-dom';
import './TopBullions.scss';

// Definicja typu dla poszczeg贸lnych element贸w
interface Bullion {
    title: string;
    image: string;
    link: string;
}

const bullions: Bullion[] = [
    {
        title: 'Gold',
        image: '/assets/images/gold.jpg',
        link: '/guides/gold'
    },
    {
        title: 'Silver',
        image: '/assets/images/silver.jpg',
        link: '/guides/silver'
    },
    {
        title: 'Platinum',
        image: '/assets/images/platinum.jpg',
        link: '/guides/platinum'
    }
];

const TopBullions: React.FC = () => {
    return (
        <section className="top-bullions">
            <h2>Top Bullions</h2>
            <div className="bullion-container">
                {bullions.map((bullion, index) => (
                    <div key={index} className="bullion-card">
                        <img src={bullion.image} alt={bullion.title} className="bullion-image" />
                        <h3>{bullion.title}</h3>
                        <Link to={bullion.link} className="learn-more">Learn more</Link>
                    </div>
                ))}
            </div>
        </section>
    );
};

export default TopBullions;
