import React, { useEffect, useState } from 'react';
import Slider from 'react-slick';
import { Link } from 'react-router-dom';
import './Recommendation.scss';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { useTranslation } from 'react-i18next';

interface Product {
    id: number;
    name: string;
    price?: {
        regularPrice?: {
            amount?: {
                value?: number;
                currency?: string;
            };
        };
    };
    sku: string;
    small_image?: {
        url?: string;
        label?: string;
    };
}

const Recommendation: React.FC = () => {
    const { t } = useTranslation();

    const [products, setProducts] = useState<Product[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [favorites, setFavorites] = useState<number[]>([]);
    const [message, setMessage] = useState<string | null>(null);
    const [wishlistId, setWishlistId] = useState<number | null>(null);

    const addToWishlist = async (sku: string) => {
        const token = sessionStorage.getItem('auth_token');
        if (!token || !wishlistId) return;

        const mutation = `
            mutation {
                addProductsToWishlist(
                    wishlistId: ${wishlistId}
                    wishlistItems: [
                        { sku: "${sku}", quantity: 1 }
                    ]
                ) {
                    wishlist {
                        id
                        items_count
                        items {
                            id
                            qty
                            product {
                                name
                                sku
                                id
                            }
                        }
                    }
                }
            }
        `;

        try {
            const response = await fetch('http://mint.local/graphql', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({ query: mutation }),
            });

            if (response.ok) {
                setMessage('Produkt został dodany do listy życzeń!');
            }
        } finally {
            setTimeout(() => setMessage(null), 3000);
        }
    };

    const toggleFavorite = (product: Product) => {
        const token = sessionStorage.getItem('auth_token');

        if (!token) {
            setMessage('Musisz być zalogowany, aby dodać produkt do listy życzeń.');
            setTimeout(() => setMessage(null), 3000);
            return;
        }

        if (favorites.includes(product.id)) {
            setFavorites(favorites.filter(favId => favId !== product.id));
        } else {
            setFavorites([...favorites, product.id]);
            addToWishlist(product.sku);
        }
    };

    useEffect(() => {
        const fetchProducts = async () => {
            const query = `
                {
                    products(search: "") {
                        items {
                            id
                            name
                            sku
                            price {
                                regularPrice {
                                    amount {
                                        value
                                        currency
                                    }
                                }
                            }
                            small_image {
                                url
                                label
                            }
                        }
                    }
                }
            `;

            try {
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ query }),
                });

                if (response.ok) {
                    const data = await response.json();
                    setProducts(data.data.products.items);
                }
            } finally {
                setLoading(false);
            }
        };

        const fetchWishlistId = async () => {
            const token = sessionStorage.getItem('auth_token');
            if (!token) return;

            const query = `
                {
                    customer {
                        wishlist {
                            id
                        }
                    }
                }
            `;

            try {
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`,
                    },
                    body: JSON.stringify({ query }),
                });

                if (response.ok) {
                    const data = await response.json();
                    if (data.data.customer.wishlist) {
                        setWishlistId(data.data.customer.wishlist.id);
                    }
                }
            } catch (error) {
                console.error("Błąd podczas pobierania wishlistId:", error);
            }
        };

        fetchProducts();
        fetchWishlistId(); // Tylko dla zalogowanych użytkowników
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    const settings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 1,
        nextArrow: <div style={{ display: 'none' }} />,
        prevArrow: <div style={{ display: 'none' }} />,
    };

    return (
        <div className="recommendationSection">
            <h1>{t('header.recommendations')}</h1>

            {message && <div className="notification">{message}</div>}

            <Slider {...settings}>
                {products.map(product => (
                    <div key={product.id} className="product-card">
                        {product.small_image && product.small_image.url ? (
                            <img src={product.small_image.url} alt={product.small_image.label} className="product-image" />
                        ) : (
                            <p>No image available</p>
                        )}
                        <h2>{product.name}</h2>
                        {product.price && product.price.regularPrice ? (
                            <p>
                                Price: {product.price.regularPrice.amount?.value} {product.price.regularPrice.amount?.currency}
                            </p>
                        ) : (
                            <p>Price not available</p>
                        )}

                        <div className="product-icons">
                            <span
                                className={`icon-heart ${favorites.includes(product.id) ? 'active' : ''}`}
                                onClick={() => toggleFavorite(product)}
                            >
                                ♥
                            </span>
                            <Link to={`/product/${product.sku}`}>
                                <i className="fas fa-search icon-loupe"></i>
                            </Link>
                        </div>
                    </div>
                ))}
            </Slider>
        </div>
    );
};

export default Recommendation;
