import React, { useEffect, useState } from 'react';
import './Platinum.scss';

interface Product {
    id: number;
    name: string;
    price: {
        regularPrice: {
            amount: {
                value: number;
                currency: string;
            };
        };
    };
    small_image?: {
        url?: string;
        label?: string;
    };
}

const Platinum: React.FC = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchPlatinumProducts = async () => {
            const query = `
                {
                    products(
                        filter: {
                            name: {
                                match: "platinum"
                            }
                        }
                    ) {
                        items {
                            id
                            name
                            price {
                                regularPrice {
                                    amount {
                                        value
                                        currency
                                    }
                                }
                            }
                            small_image {
                                url
                                label
                            }
                        }
                    }
                }
            `;

            try {
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ query }),
                });

                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }

                const data = await response.json();
                setProducts(data.data.products.items.slice(0, 5)); // Limit to 5 products
            } catch (err) {
                if (err instanceof Error) {
                    setError(err.message);
                } else {
                    setError('An unknown error occurred');
                }
            } finally {
                setLoading(false);
            }
        };

        fetchPlatinumProducts();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="platinum-container">
            <h1>Platinum Bullion</h1>
            <div className="platinum-history-facts">
                <div className="platinum-history">
                    <h2>History of Platinum</h2>
                    <p>Platinum was first discovered in the 18th century and was considered a precious metal long before gold.</p>
                    <p>Throughout history, platinum has often been used in coins and bullion, prized for its weight and value.</p>
                    <p>Today, platinum is also used in catalytic converters and in various electronics, showcasing its versatility.</p>
                    <p></p>
                </div>
                <div className="platinum-facts">
                    <h2>Fun Facts about Platinum</h2>
                    <p>Platinum is much denser than gold, making it heavier and more durable.</p>
                    <p>Unlike gold, platinum is not affected by air or moisture, which means it doesnâ€™t tarnish.</p>
                    <p>The largest platinum nugget ever found weighed over 2 kilograms and was discovered in South America.</p>
                    <p></p>
                </div>
            </div>

            <h2>Check these products:</h2>
            <div className="product-carousel">
                {products.map(product => (
                    <div key={product.id} className="product-card">
                        {product.small_image && product.small_image.url ? (
                            <img src={product.small_image.url} alt={product.small_image.label} className="product-image" />
                        ) : (
                            <p>No image available</p>
                        )}
                        <h3>{product.name}</h3>
                        <p>Price: {product.price.regularPrice.amount.value} {product.price.regularPrice.amount.currency}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Platinum;
