import React, { useEffect, useState } from 'react';
import './Gold.scss';

interface Product {
    id: number;
    name: string;
    price: {
        regularPrice: {
            amount: {
                value: number;
                currency: string;
            };
        };
    };
    small_image?: {
        url?: string;
        label?: string;
    };
}

const Gold: React.FC = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchGoldProducts = async () => {
            const query = `
                {
                    products(
                        filter: {
                            name: {
                                match: "gold"
                            }
                        }
                    ) {
                        items {
                            id
                            name
                            price {
                                regularPrice {
                                    amount {
                                        value
                                        currency
                                    }
                                }
                            }
                            small_image {
                                url
                                label
                            }
                        }
                    }
                }
            `;

            try {
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ query }),
                });

                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }

                const data = await response.json();
                setProducts(data.data.products.items.slice(0, 5)); // Limit to 5 products
            } catch (err) {
                if (err instanceof Error) {
                    setError(err.message);
                } else {
                    setError('An unknown error occurred');
                }
            } finally {
                setLoading(false);
            }
        };

        fetchGoldProducts();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="gold-container">
            <h1>Gold Bullion</h1>
            <div className="gold-history-facts">
                <div className="gold-history">
                    <h2>History of Gold</h2>
                    <p>Gold has been a symbol of wealth and prosperity throughout history. It is used in jewelry, electronics, and as a form of currency.</p>
                    <p>The history of gold dates back thousands of years, used by ancient civilizations for trade and ornamentation.</p>
                    <p>Gold is often associated with the sun, symbolizing purity and divinity.</p>
                    <p>Investors often turn to gold during times of economic uncertainty as a safe-haven asset.</p>
                    <p>Today, gold continues to play a critical role in global economies and financial systems.</p>
                </div>
                <div className="gold-facts">
                    <h2>Fun Facts about Gold</h2>
                    <p>Gold is the most malleable metal; a single gram can be stretched into a wire over 2 kilometers long.</p>
                    <p>Pure gold is actually a bright yellow color and is often alloyed with other metals to create various colors.</p>
                    <p>Gold is a non-renewable resource, and mining it can have significant environmental impacts.</p>
                    <p>The largest gold nugget ever found weighed over 200 kg and was discovered in Australia.</p>
                    <p>Gold has been used as a form of currency for thousands of years, and many cultures have their own gold coins.</p>
                </div>
            </div>

            <h1>Check these products:</h1>
            <div className="product-carousel">
                {products.map(product => (
                    <div key={product.id} className="product-card">
                        {product.small_image && product.small_image.url ? (
                            <img src={product.small_image.url} alt={product.small_image.label} className="product-image" />
                        ) : (
                            <p>No image available</p>
                        )}
                        <h3>{product.name}</h3>
                        <p>Price: {product.price.regularPrice.amount.value} {product.price.regularPrice.amount.currency}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Gold;
