import React, { useEffect } from 'react';
import { useCart } from '../../context/CartContext';
import { useUserContext } from '../../context/UserContext';

const CartPage: React.FC = () => {
    const { items, removeItem, updateQuantity, fetchCart } = useCart();
    const { isLoggedIn } = useUserContext();

    useEffect(() => {
        if (isLoggedIn) {
            fetchCart();
        }
    }, [isLoggedIn]);

    return (
        <div>
            <h1>Your Cart</h1>
            {items.length === 0 ? (
                <p>Your cart is empty</p>
            ) : (
                <ul>
                    {items.map((item) => (
                        <li key={item.itemId}>
                            <h2>{item.name}</h2>
                            <p>Price: ${item.price}</p>
                            <p>Quantity: {item.quantity}</p>
                            <button onClick={() => removeItem(item.itemId)}>Remove</button>
                            <button onClick={() => updateQuantity(item.itemId, item.quantity + 1)}>+</button>
                            <button onClick={() => updateQuantity(item.itemId, item.quantity - 1)} disabled={item.quantity <= 1}>-</button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default CartPage;
