import React, { useEffect, useState } from 'react';
import Slider from 'react-slick';
import { Link } from 'react-router-dom';
import './Recommendation.scss';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { useTranslation } from 'react-i18next';
import WishlistButton from '../../components/WishlistButton/WishlistButton';

interface Product {
    id: number;
    name: string;
    price?: {
        regularPrice?: {
            amount?: {
                value?: number;
                currency?: string;
            };
        };
    };
    sku: string;
    small_image?: {
        url?: string;
        label?: string;
    };
}

const Recommendation: React.FC = () => {
    const { t } = useTranslation();
    const [products, setProducts] = useState<Product[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [favorites, setFavorites] = useState<number[]>([]);
    const [wishlistId, setWishlistId] = useState<number | null>(null);
    const [windowWidth, setWindowWidth] = useState<number>(window.innerWidth);

    const toggleFavorite = (productId: number) => {
        if (favorites.includes(productId)) {
            setFavorites(favorites.filter(favId => favId !== productId));
        } else {
            setFavorites([...favorites, productId]);
        }
    };

    useEffect(() => {
        const fetchProducts = async () => {
            const query = `
                {
                    products(search: "") {
                        items {
                            id
                            name
                            sku
                            price {
                                regularPrice {
                                    amount {
                                        value
                                        currency
                                    }
                                }
                            }
                            small_image {
                                url
                                label
                            }
                        }
                    }
                }
            `;

            try {
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ query }),
                });

                if (response.ok) {
                    const data = await response.json();
                    setProducts(data.data.products.items);
                }
            } finally {
                setLoading(false);
            }
        };

        const fetchWishlistId = async () => {
            const token = sessionStorage.getItem('auth_token');
            if (!token) return;

            const query = `
                {
                    customer {
                        wishlist {
                            id
                        }
                    }
                }
            `;

            try {
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`,
                    },
                    body: JSON.stringify({ query }),
                });

                if (response.ok) {
                    const data = await response.json();
                    if (data.data.customer.wishlist) {
                        setWishlistId(data.data.customer.wishlist.id);
                    }
                }
            } catch (error) {
                console.error("Błąd podczas pobierania wishlistId:", error);
            }
        };

        fetchProducts();
        fetchWishlistId();

        const handleResize = () => setWindowWidth(window.innerWidth);
        window.addEventListener('resize', handleResize);

        return () => window.removeEventListener('resize', handleResize);
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    const sliderSettings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 5, // Ustawienie na 5 produktów w jednej linii
        slidesToScroll: 1,
        customPaging: (i: number) => (
            <div className="dot">
                <span className="dot-number">{i + 1}</span>
            </div>
        ),
        dotsClass: "slick-dots custom-dots", // Niestandardowa klasa dla kropek
    };

    return (
        <div className="recommendationSection">
            <h1>{t('header.recommendations')}</h1>

            <Slider {...sliderSettings}>
                {products.map(product => (
                    <div key={product.id} className="product-card">
                        {product.small_image && product.small_image.url ? (
                            <img src={product.small_image.url} alt={product.small_image.label} className="product-image" />
                        ) : (
                            <p>No image available</p>
                        )}
                        <h2>{product.name}</h2>
                        {product.price && product.price.regularPrice ? (
                            <p>
                                Price: {product.price.regularPrice.amount?.value} {product.price.regularPrice.amount?.currency}
                            </p>
                        ) : (
                            <p>Price not available</p>
                        )}

                        <div className="product-icons">
                            <WishlistButton
                                productId={product.id}
                                sku={product.sku}
                                wishlistId={wishlistId}
                                favorites={favorites}
                                toggleFavorite={toggleFavorite}
                            />
                            <Link to={`/product/${product.sku}`}>
                                <i className="fas fa-search icon-loupe"></i>
                            </Link>
                        </div>
                    </div>
                ))}
            </Slider>
        </div>
    );
};

export default Recommendation;
