import React, { useState } from 'react';
import './Login.scss';
import { Link } from 'react-router-dom';

const Login: React.FC = () => {
    const [showPassword, setShowPassword] = useState(false);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const validateEmail = (email: string) => {
        const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return re.test(String(email).toLowerCase());
    };

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();

        setError('');
        setSuccess('');

        if (!validateEmail(email)) {
            setError('Please enter a valid email address');
            return;
        }

        if (password.length < 6) {
            setError('Password must be at least 6 characters long');
            return;
        }

        const query = `
            mutation {
                generateCustomerToken(
                    email: "${email}",
                    password: "${password}"
                ) {
                    token
                }
            }
        `;

        try {
            const response = await fetch('http://mint.local/graphql', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ query }),
            });

            const result = await response.json();

            if (result.data && result.data.generateCustomerToken.token) {
                const token = result.data.generateCustomerToken.token;
                sessionStorage.setItem('auth_token', token);
                setSuccess('Login successful!');
            } else {
                setError('Invalid login credentials');
            }
        } catch (err) {
            console.error(err);
            setError('An error occurred while logging in. Please try again.');
        }
    };

    return (
        <div className="login-page">
            <h1>Customer Login</h1>

            <form className="login-form" onSubmit={handleLogin}>
                <label htmlFor="email">Email</label>
                <input
                    type="email"
                    id="email"
                    name="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />

                <label htmlFor="password">Password</label>
                <div className="password-input-wrapper">
                    <input
                        type={showPassword ? 'text' : 'password'}
                        id="password"
                        name="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <span
                        className="toggle-password-visibility"
                        onClick={() => setShowPassword(!showPassword)}
                    >
                        <i className={`fas ${showPassword ? 'fa-eye-slash' : 'fa-eye'}`}></i>
                    </span>
                </div>

                {error && <p className="error">{error}</p>}
                {success && <p className="success">{success}</p>}

                <div className="actions">
                    <button type="submit" className="sign-in-button">Sign in</button>
                    <a href="#" className="forgot-password">Forgot your password?</a>
                </div>
            </form>

            <div className="divider"></div>

            <div className="new-customer">
                <h2>New customers</h2>
                <p>Creating an account has many benefits: check out faster, keep more than one address, track orders and more.</p>
                <Link to="/signup"><button className="create-account-button">Create an Account</button></Link>
            </div>
        </div>
    );
};

export default Login;
