import React, { useEffect, useState } from 'react';
import './Silver.scss';

interface Product {
    id: number;
    name: string;
    price: {
        regularPrice: {
            amount: {
                value: number;
                currency: string;
            };
        };
    };
    small_image?: {
        url?: string;
        label?: string;
    };
}

const Silver: React.FC = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchSilverProducts = async () => {
            const query = `
                {
                    products(
                        filter: {
                            name: {
                                match: "silver"
                            }
                        }
                    ) {
                        items {
                            id
                            name
                            price {
                                regularPrice {
                                    amount {
                                        value
                                        currency
                                    }
                                }
                            }
                            small_image {
                                url
                                label
                            }
                        }
                    }
                }
            `;

            try {
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ query }),
                });

                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }

                const data = await response.json();
                setProducts(data.data.products.items.slice(0, 5)); // Limit to 5 products
            } catch (err) {
                if (err instanceof Error) {
                    setError(err.message);
                } else {
                    setError('An unknown error occurred');
                }
            } finally {
                setLoading(false);
            }
        };

        fetchSilverProducts();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="silver-container">
            <h1>Silver Bullion</h1>
            <div className="silver-history-facts">
                <div className="silver-history">
                    <h2>History of Silver</h2>
                    <p>Silver has been used for thousands of years as a form of currency, jewelry, and in various
                        industrial applications.</p>
                    <p>Throughout history, silver has been used to create coins and has played a significant role in
                        global trade.</p>
                    <p>Today, silver is still a popular investment option, often viewed as a safe-haven asset.</p>
                    <p></p>
                </div>
                <div className="silver-facts">
                    <h2>Fun Facts about Silver</h2>
                    <p>Silver is the best conductor of electricity and is used in electronic devices and batteries.</p>
                    <p>Unlike gold, silver tarnishes when exposed to air and moisture, resulting in a blackish
                        color.</p>
                    <p>The largest silver nugget ever discovered weighed over 1000 kg and was found in the 19th
                        century.</p>
                    <p></p>
                </div>
            </div>

            <h2>Check these products:</h2>
            <div className="product-carousel">
                {products.map(product => (
                    <div key={product.id} className="product-card">
                        {product.small_image && product.small_image.url ? (
                            <img src={product.small_image.url} alt={product.small_image.label} className="product-image" />
                        ) : (
                            <p>No image available</p>
                        )}
                        <h3>{product.name}</h3>
                        <p>Price: {product.price.regularPrice.amount.value} {product.price.regularPrice.amount.currency}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Silver;
