// CartContext.tsx
import React, { createContext, useContext, useReducer, ReactNode } from 'react';

interface CartItem {
    sku: string;
    name: string;
    price: number;
    quantity: number;
}

interface CartState {
    items: CartItem[];
}

type CartAction =
    | { type: 'ADD_TO_CART'; item: CartItem }
    | { type: 'REMOVE_FROM_CART'; sku: string }
    | { type: 'UPDATE_QUANTITY'; sku: string; quantity: number };

const CartContext = createContext<{ state: CartState; dispatch: React.Dispatch<CartAction> } | undefined>(undefined);

const cartReducer = (state: CartState, action: CartAction): CartState => {
    switch (action.type) {
        case 'ADD_TO_CART':
            const existingItem = state.items.find(item => item.sku === action.item.sku);
            if (existingItem) {
                return {
                    ...state,
                    items: state.items.map(item =>
                        item.sku === action.item.sku
                            ? { ...item, quantity: item.quantity + action.item.quantity }
                            : item
                    ),
                };
            }
            return { ...state, items: [...state.items, action.item] };
        case 'REMOVE_FROM_CART':
            return { ...state, items: state.items.filter(item => item.sku !== action.sku) };
        case 'UPDATE_QUANTITY':
            return {
                ...state,
                items: state.items.map(item =>
                    item.sku === action.sku ? { ...item, quantity: action.quantity } : item
                ),
            };
        default:
            return state;
    }
};

export const CartProvider = ({ children }: { children: ReactNode }) => {
    const [state, dispatch] = useReducer(cartReducer, { items: [] });
    return <CartContext.Provider value={{ state, dispatch }}>{children}</CartContext.Provider>;
};

export const useCart = () => {
    const context = useContext(CartContext);
    if (!context) {
        throw new Error('useCart must be used within a CartProvider');
    }
    return context;
};
