import React, { useEffect, useState } from 'react';

interface Product {
    id: number;
    name: string;
    price: number;
}

const TopSellers: React.FC = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('http://mint.local/rest/V1/products?searchCriteria%5BpageSize%5D=1&searchCriteria%5BceyJraWQiOiIxIiwiYWxnIjoiSFMyNTYifQ.eyJ1aWQiOjEsInV0eXBpZCI6MiwiaWF0IjoxNzI5NzYxMDc3LCJleHAiOjE3Mjk3NjQ2Nzd9.MnvHRrin74-8h1ktHfd1tbTJGUuFYDGIgZMRtShKqjcurrentPage%5D=1', {
                    method: 'GET',
                    headers: {
                        'Authorization': 'Bearer eyeeyJraWQiOiIxIiwiYWxnIjoiSFMyNTYifQ.eyJ1aWQiOjEsInV0eXBpZCI6MiwiaWF0IjoxNzI5NzYxMDc3LCJleHAiOjE3Mjk3NjQ2Nzd9.MnvHRrin74-8h1ktHfd1tbTJGUuFYDGIgZMRtShKqjcyJraWQiOiIxIiwiYWxnIjoiSFMyNTYifQ.eyJ1aWQiOjEsInV0eXBpZCI6MiwiaWF0IjoxNzI5NzU2OTEyLCJleHAiOjE3Mjk3NjA1MTJ9.lkP03SJbnTVQAXMJsPXNg8rp3vGifsr_YOPwonDPM7YJraWQiOiIxIiwiYWxnIjoiSFMyNTYifQ.eyJ1aWQiOjEsInV0eXBpZCI6MiwiaWF0IjoxNzI5NzYxMDc3LCJleHAiOjE3Mjk3NjQ2Nzd9.MnvHRrin74-8h1ktHfd1tbTJGUuFYDGIgZMRtShKqjc',
                        'Content-Type': 'application/json',
                    },
                });
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setProducts(data.items);
            } catch (err: unknown) {
                if (err instanceof Error) {
                    setError(err.message);
                } else {
                    setError('An unknown error occurred');
                }
            } finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <h1>Top Sellers</h1>
            <div className="product-list">
                {products.map(product => (
                    <div key={product.id} className="product-card">
                        <h2>{product.name}</h2>
                        <p>Price: ${product.price.toFixed(2)}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default TopSellers;
