import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../../context/CartContext';

const PaymentPage: React.FC = () => {
    const { shippingAddress, shippingMethod, paymentMethod, setShippingMethod, setPaymentMethod, placeOrder, clearCart } = useCart();
    const [error, setError] = useState<string | null>(null);
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    // Funkcja zmiany metody wysyłki
    const handleShippingMethodChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
        setShippingMethod(e.target.value);
    };

    // Funkcja zmiany metody płatności
    const handlePaymentMethodChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setPaymentMethod(e.target.value);
    };

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        setIsLoading(true);
        setError(null);

        try {
            if (shippingAddress && shippingMethod && paymentMethod) {
                // Jeśli wszystkie dane są dostępne, przejdź do złożenia zamówienia
                await placeOrder();
                clearCart();
                navigate(`/order/success`); // Przekierowanie na stronę sukcesu zamówienia
            } else {
                setError("Wszystkie dane (adres dostawy, metoda wysyłki, metoda płatności) są wymagane.");
            }
        } catch (error) {
            setError("Wystąpił błąd podczas składania zamówienia.");
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div>
            <h2>Płatność</h2>
            {/* Wyświetlenie adresu wysyłki */}
            {shippingAddress && (
                <div>
                    <h3>Adres wysyłki</h3>
                    <p>{shippingAddress.firstName} {shippingAddress.lastName}</p>
                    <p>{shippingAddress.street.join(', ')}</p>
                    <p>{shippingAddress.city}, {shippingAddress.region}</p>
                    <p>{shippingAddress.postalCode}</p>
                    <p>{shippingAddress.telephone}</p>
                </div>
            )}

            <form onSubmit={handleSubmit}>
                <div>
                    <h3>Metoda wysyłki</h3>
                    <select
                        onChange={handleShippingMethodChange}
                        value={shippingMethod || ''}
                        required
                    >
                        <option value="">Wybierz metodę wysyłki</option>
                        <option value="standard">Standardowa</option>
                        <option value="express">Ekspresowa</option>
                    </select>
                </div>

                <div>
                    <h3>Metoda płatności</h3>
                    <label>
                        <input
                            type="radio"
                            value="checkmo"
                            checked={paymentMethod === 'checkmo'}
                            onChange={handlePaymentMethodChange}
                        />
                        Płatność przy odbiorze
                    </label>
                </div>

                {error && <div>{error}</div>}
                <button type="submit" disabled={isLoading}>Złóż zamówienie</button>
            </form>
        </div>
    );
};

export default PaymentPage;
