import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useCart } from '../../context/CartContext';
import './ProductDetail.scss';

interface Product {
    id: number;
    name: string;
    sku: string;
    price: {
        regularPrice: {
            amount: {
                value: number;
                currency: string;
            };
        };
    };
    stock_status?: string;
    small_image?: {
        url?: string;
        label?: string;
    };
    short_description?: {
        html?: string;
    };
}

const ProductDetail: React.FC = () => {
    const { sku } = useParams<{ sku: string }>();
    const [product, setProduct] = useState<Product | null>(null);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);
    const [wishlistId, setWishlistId] = useState<number | null>(null);
    const [message, setMessage] = useState<string | null>(null);
    const [quantity, setQuantity] = useState<number>(1);
    const { dispatch } = useCart();

    useEffect(() => {
        const fetchProductDetails = async () => {
            const query = `
                {
                    products(
                        filter: {
                            sku: {
                                eq: "${sku}"
                            }
                        },
                        pageSize: 1,
                        currentPage: 1
                    ) {
                        items {
                            id
                            sku
                            name
                            stock_status
                            short_description {
                                html
                            }
                            price {
                                regularPrice {
                                    amount {
                                        value
                                        currency
                                    }
                                }
                            }
                            small_image {
                                url
                                label
                            }
                        }
                    }
                    customer {
                        wishlist {
                            id
                        }
                    }
                }
            `;

            try {
                const token = sessionStorage.getItem('auth_token');
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`,
                    },
                    body: JSON.stringify({ query }),
                });

                if (!response.ok) {
                    throw new Error('Failed to fetch product details');
                }

                const data = await response.json();
                setProduct(data.data.products.items[0]);
                if (data.data.customer.wishlist) {
                    setWishlistId(data.data.customer.wishlist.id);
                }
            } catch (err) {
                setError(err instanceof Error ? err.message : 'An unknown error occurred');
            } finally {
                setLoading(false);
            }
        };

        if (sku) {
            fetchProductDetails();
        }
    }, [sku]);

    const addToWishlist = async () => {
        if (!product || !wishlistId) return;

        const token = sessionStorage.getItem('auth_token');
        const mutation = `
            mutation {
                addProductsToWishlist(
                    wishlistId: ${wishlistId}
                    wishlistItems: [
                        { sku: "${product.sku}", quantity: 1 }
                    ]
                ) {
                    wishlist {
                        id
                        items_count
                    }
                }
            }
        `;

        try {
            const response = await fetch('http://mint.local/graphql', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({ query: mutation }),
            });

            if (response.ok) {
                setMessage('Produkt został dodany do listy życzeń!');
                setTimeout(() => setMessage(null), 3000);
            }
        } catch (err) {
            console.error("Failed to add to wishlist", err);
        }
    };

    const handleQuantityChange = (change: number) => {
        if (product?.stock_status === 'OUT_OF_STOCK') return;
        const maxQuantity = product ? 10 : 1; // Przykład: przyjęto 10 jako maksymalna dostępna ilość
        setQuantity(prev => Math.max(1, Math.min(prev + change, maxQuantity)));
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    const addToCart = () => {
        if (product) {
            dispatch({
                type: 'ADD_TO_CART',
                item: {
                    sku: product.sku,
                    name: product.name,
                    price: product.price.regularPrice.amount.value,
                    quantity,
                },
            });
        }
    };

    return (
        <div className="product-detail">
            <div className="breadcrumb">
                <Link to="/">Home</Link> &gt; <Link to="/category">Category</Link> &gt; {product?.name}
            </div>

            {product && (
                <div className="product-detail-container">
                    <div className="product-image">
                        {product.small_image && product.small_image.url ? (
                            <img src={product.small_image.url} alt={product.small_image.label} />
                        ) : (
                            <p>No image available</p>
                        )}
                    </div>
                    <div className="product-info">
                        <h1 className="product-title">{product.name}</h1>
                        <div className="price-stock-sku">
                            <span className="product-price">
                                ${product.price.regularPrice.amount.value.toFixed(2)}
                            </span>
                            <span className={`product-stock ${product.stock_status === 'IN_STOCK' ? 'in-stock' : 'out-of-stock'}`}>
                                {product.stock_status === 'IN_STOCK' ? 'IN STOCK' : 'OUT OF STOCK'}
                            </span>
                            <span className="product-sku">SKU: {product.sku}</span>
                        </div>
                        <div className="quantity">
                            <label>Quantity: </label>
                            <button onClick={() => handleQuantityChange(-1)} disabled={quantity <= 1}>-</button>
                            <span>{quantity}</span>
                            <button onClick={() => handleQuantityChange(1)} disabled={quantity >= 10}>+</button>
                        </div>
                        <div className="actions">
                            <button className="add-to-cart" onClick={addToCart}>Add to Cart</button>
                            <button className="wishlist-button" onClick={addToWishlist}>
                                ♥ Add to Wishlist
                            </button>
                        </div>
                        <div className="details">
                            <h2>Details</h2>
                            <div
                                className="short-description"
                                dangerouslySetInnerHTML={{ __html: product.short_description?.html || '' }}
                            />
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ProductDetail;
