import React, { useEffect, useState } from 'react';
import './TopSellers.scss';

interface Product {
    id: number;
    name: string;
    price?: {
        regularPrice?: {
            amount?: {
                value?: number;
                currency?: string;
            };
        };
    };
}

const TopSellers: React.FC = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchProducts = async () => {
            const query = `
                {
                    products(search: "") {
                        items {
                            id
                            name
                            price {
                                regularPrice {
                                    amount {
                                        value
                                        currency
                                    }
                                }
                            }
                        }
                        total_count
                        page_info {
                            page_size
                            current_page
                        }
                    }
                }
            `;

            try {
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ query }),
                });

                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }

                const data = await response.json();
                setProducts(data.data.products.items);
            } catch (err: unknown) {
                if (err instanceof Error) {
                    setError(err.message);
                } else {
                    setError('An unknown error occurred');
                }
            } finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="topSellersSection">
            <h1>Top Sellers</h1>
            <div className="product-container"> {/* Użycie kontenera dla produktów */}
                {products.map(product => (
                    <div key={product.id} className="product-card">
                        <h2>{product.name}</h2>
                        {product.price && product.price.regularPrice ? (
                            <p>
                                Price: {product.price.regularPrice.amount?.value} {product.price.regularPrice.amount?.currency}
                            </p>
                        ) : (
                            <p>Price not available</p>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default TopSellers;
