import React, { useState } from 'react';
import './Signup.scss';
import { useTranslation } from 'react-i18next';

const Signup: React.FC = () => {
    const { t } = useTranslation();

    const [showPassword, setShowPassword] = useState(false);
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handlePasswordToggle = () => {
        setShowPassword(!showPassword);
    };

    const handleSignup = async (e: React.FormEvent) => {
        e.preventDefault();

        setError('');
        setSuccessMessage('');

        const query = `
            mutation {
                createCustomer(
                    input: {
                        email: "${email}",
                        firstname: "${firstName}",
                        lastname: "${lastName}",
                        password: "${password}"
                    }
                ) {
                    customer {
                        id
                        email
                        firstname
                        lastname
                    }
                }
            }
        `;

        try {
            const response = await fetch('http://mint.local/graphql', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ query }),
            });

            const result = await response.json();

            if (result.data.createCustomer.customer) {
                setSuccessMessage(t('signup.account_created'));
                setFirstName('');
                setLastName('');
                setEmail('');
                setPassword('');
            } else {
                const userErrors = result.data.createCustomer.userErrors;
                if (userErrors && userErrors.length > 0) {
                    setError(userErrors[0].message);
                } else {
                    setError(t('signup.error_unknown'));
                }
            }
        } catch (err) {
            setError(t('signup.error_create_account'));
        }
    };

    return (
        <div className="signup-container">
            <h1>{t('signup.title')}</h1>
            <form className="signup-form" onSubmit={handleSignup}>
                <div className="form-group">
                    <label htmlFor="firstName">
                        {t('signup.first_name')} <span className="required">*</span>
                    </label>
                    <input
                        type="text"
                        id="firstName"
                        placeholder=" "
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="lastName">
                        {t('signup.last_name')} <span className="required">*</span>
                    </label>
                    <input
                        type="text"
                        id="lastName"
                        placeholder=" "
                        value={lastName}
                        onChange={(e) => setLastName(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="email">
                        {t('signup.email')} <span className="required">*</span>
                    </label>
                    <input
                        type="email"
                        id="email"
                        placeholder=" "
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="password">
                        {t('signup.password')} <span className="required">*</span>
                    </label>
                    <div className="password-input-wrapper">
                        <input
                            type={showPassword ? 'text' : 'password'}
                            id="password"
                            placeholder=" "
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                        <span
                            className="toggle-password-visibility"
                            onClick={handlePasswordToggle}
                        >
                            <i className={`fas ${showPassword ? 'fa-eye-slash' : 'fa-eye'}`}></i>
                        </span>
                    </div>
                </div>

                <div className="form-group">
                    <label className="checkbox-option">
                        <input type="checkbox" />
                        {t('signup.sign_up_newsletter')}
                    </label>
                </div>

                <div className="form-group">
                    <label className="checkbox-option">
                        <input type="checkbox" />
                        {t('signup.remote_shopping')}
                    </label>
                </div>

                {error && <p className="error">{error}</p>}
                {successMessage && <p className="success">{successMessage}</p>}

                <button type="submit" className="submit-button">
                    {t('signup.create_account_button')}
                </button>

                <p className="required-info">
                    {t('signup.required_info')} <span className="required">*</span>
                </p>
            </form>
        </div>
    );
};

export default Signup;
