import React, { useState } from 'react';
import './NewsLetterSubPage.scss';
import UserSidebar from '../../components/UserSidebar/UserSidebar'; // Import UserSidebar

const NewsLetterSubPage: React.FC = () => {
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        // Add logic to submit the email for the newsletter
        setMessage('Thank you for subscribing!');
        setEmail('');
    };

    return (
        <div className="newsletter-sub-page-container">
            <div className="sidebar-container">
                <UserSidebar /> {/* UserSidebar po lewej stronie */}
            </div>
            <div className="content-container">
                <h2>Subscribe to our Newsletter</h2>
                <form onSubmit={handleSubmit}>
                    <label>
                        Email:
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </label>
                    <button type="submit">Subscribe</button>
                </form>
                {message && <p className="success-message">{message}</p>}
            </div>
        </div>
    );
};

export default NewsLetterSubPage;
