import React, { useEffect, useState } from 'react';
import UserSidebar from '../../components/UserSidebar/UserSidebar';
import './Wishlist.scss';

interface Product {
    id: string;
    name: string;
    sku: string;
    price: {
        regularPrice: {
            amount: {
                value: number;
                currency: string;
            };
        };
    };
}

interface WishlistItem {
    id: string;
    product: Product;
}

interface Wishlist {
    id: string;
    items: WishlistItem[];
}

const Wishlist: React.FC = () => {
    const [wishlists, setWishlists] = useState<Wishlist[]>([]); // Typ dla wishlists
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchWishlists = async () => {
            const token = sessionStorage.getItem('auth_token');
            if (!token) {
                setError("User is not logged in.");
                setLoading(false);
                return;
            }

            const query = `
                query {
                    customer {
                        wishlists {
                            id
                            items {
                                id
                                product {
                                    id
                                    name
                                    sku
                                    price {
                                        regularPrice {
                                            amount {
                                                value
                                                currency
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            `;

            try {
                const response = await fetch('http://mint.local/graphql', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`,
                    },
                    body: JSON.stringify({ query }),
                });

                const result = await response.json();
                if (result.data && result.data.customer) {
                    setWishlists(result.data.customer.wishlists);
                } else {
                    setError("Failed to fetch wishlists.");
                }
            } catch (err) {
                setError("Error fetching wishlists: " + err);
            } finally {
                setLoading(false);
            }
        };

        fetchWishlists();
    }, []);

    const removeFromWishlist = async (wishlistId: string, itemId: string) => {
        const token = sessionStorage.getItem('auth_token');
        if (!token) {
            setError("User is not logged in.");
            return;
        }

        const mutation = `
            mutation {
                removeItemFromWishlist(wishlistId: "${wishlistId}", itemId: "${itemId}") {
                    id
                    items {
                        id
                    }
                }
            }
        `;

        try {
            const response = await fetch('http://mint.local/graphql', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({ query: mutation }),
            });

            const result = await response.json();

            if (response.ok && result.data && result.data.removeItemFromWishlist) {
                setWishlists((prevWishlists) =>
                    prevWishlists.map((wishlist) => {
                        if (wishlist.id === wishlistId) {
                            return {
                                ...wishlist,
                                items: wishlist.items.filter(item => item.id !== itemId),
                            };
                        }
                        return wishlist;
                    })
                );
            } else {
                // Logowanie błędu
                console.error("Error response:", result);
                setError(result.errors ? result.errors[0].message : "Failed to remove item from wishlist.");
            }
        } catch (err) {
            console.error("Fetch error:", err);
            setError("Error removing item from wishlist: " + err);
        }
    };

    return (
        <div className="wish-list">
            <UserSidebar />

            <div className="wish-list__content">
                <h2>My Wish List</h2>
                <section>
                    {loading && <p>Loading...</p>}
                    {error && <p>{error}</p>}

                    {wishlists.length > 0 ? (
                        wishlists.map((wishlist) => (
                            <div key={wishlist.id}>
                                <h3>Wishlist ID: {wishlist.id}</h3>
                                <ul>
                                    {wishlist.items.map((item: WishlistItem) => ( // Typ dla parametru item
                                        <li key={item.id}>
                                            <h4>{item.product.name}</h4>
                                            <p>SKU: {item.product.sku}</p>
                                            <p>Price: {item.product.price.regularPrice.amount.value} {item.product.price.regularPrice.amount.currency}</p>
                                            <button onClick={() => removeFromWishlist(wishlist.id, item.id)}>
                                                Remove from Wishlist
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        ))
                    ) : (
                        <p>You have no items in your wish list.</p>
                    )}
                </section>
            </div>
        </div>
    );
};

export default Wishlist;
