import React, { useState } from 'react';
import { useCart } from '../../context/CartContext';
import { useNavigate } from 'react-router-dom'; // Importuj useNavigate

const ShippingForm: React.FC = () => {
    const { setShippingAddress } = useCart();
    const [address, setAddress] = useState({
        firstName: '',
        lastName: '',
        street: '',
        city: '',
        postalCode: '',
        country: '',
        telephone: ''
    });

    const navigate = useNavigate(); // Inicjalizuj useNavigate

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setAddress({
            ...address,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            await setShippingAddress(address);
            // Po ustawieniu adresu, przejdź do formularza płatności
            navigate('/payment'); // Zmień '/payment' na ścieżkę do formularza płatności
        } catch (error) {
            console.error("Wystąpił błąd podczas ustawiania adresu:", error);
            // Możesz również wyświetlić komunikat o błędzie użytkownikowi
        }
    };

    return (
        <form className="shipping-form" onSubmit={handleSubmit}>
            <h3>Adres dostawy</h3>
            <input type="text" name="firstName" placeholder="Imię" onChange={handleChange} required />
            <input type="text" name="lastName" placeholder="Nazwisko" onChange={handleChange} required />
            <input type="text" name="street" placeholder="Adres" onChange={handleChange} required />
            <input type="text" name="city" placeholder="Miasto" onChange={handleChange} required />
            <input type="text" name="postalCode" placeholder="Kod pocztowy" onChange={handleChange} required />
            <input type="text" name="country" placeholder="Kraj" onChange={handleChange} required />
            <input type="text" name="telephone" placeholder="Telefon" onChange={handleChange} required />
            <button type="submit">Przejdź do płatności</button>
        </form>
    );
};

export default ShippingForm;
