import React, { useState, useEffect } from 'react';
import { useCart } from '../../context/CartContext';
import { useNavigate } from 'react-router-dom';
import './CartPage.scss';

const CartPage: React.FC = () => {
    const { items, removeItem, updateQuantity } = useCart();
    const navigate = useNavigate();
    const [message, setMessage] = useState<string | null>(null);
    const [wishlistId, setWishlistId] = useState<number | null>(null);

    const calculateTotal = () => {
        return items.reduce((total, item) => total + item.price * item.quantity, 0);
    };

    const proceedToShipping = () => {
        navigate('/shipping');
    };

    const addToWishlist = async (item: any) => {
        const token = sessionStorage.getItem('auth_token');
        if (!token) {
            setMessage('Musisz być zalogowany, aby dodać produkt do listy życzeń.');
            setTimeout(() => setMessage(null), 3000);
            return;
        }

        if (!wishlistId) {
            setMessage('Nie znaleziono listy życzeń.');
            setTimeout(() => setMessage(null), 3000);
            return;
        }



        const query = `
            mutation {
                addProductsToWishlist(
                    wishlistId: ${wishlistId}
                    wishlistItems: [
                        { sku: "${item.sku}", quantity: 1 }
                    ]
                ) {
                    wishlist {
                        id
                        items_count
                    }
                }
            }
        `;

        try {
            const response = await fetch('http://mint.local/graphql', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({ query }),
            });

            const result = await response.json();
            console.log(result);

            if (response.ok && result.data && result.data.addProductsToWishlist) {
                setMessage('Produkt został dodany do listy życzeń!');
            } else {
                setMessage(`Błąd: ${result.errors ? result.errors[0].message : 'Nie udało się dodać do listy życzeń.'}`);
            }
        } catch (error) {
            console.error("Błąd podczas dodawania do wishlisty:", error);
            setMessage('Wystąpił błąd przy dodawaniu do listy życzeń.');
        } finally {
            setTimeout(() => setMessage(null), 3000);
        }
    };

    const fetchWishlistId = async () => {
        const token = sessionStorage.getItem('auth_token');
        if (!token) return;

        const query = `
            query {
                customer {
                    wishlist {
                        id
                    }
                }
            }
        `;

        try {
            const response = await fetch('http://mint.local/graphql', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({ query }),
            });

            const result = await response.json();
            console.log(result);

            if (response.ok && result.data && result.data.customer && result.data.customer.wishlist) {
                setWishlistId(result.data.customer.wishlist.id);
            } else {
                setMessage('Nie udało się pobrać listy życzeń użytkownika.');
            }
        } catch (error) {
            console.error('Błąd podczas pobierania wishlistId:', error);
            setMessage('Wystąpił błąd podczas pobierania listy życzeń.');
        }
    };

    useEffect(() => {
        fetchWishlistId();
    }, []);

    return (
        <div className="cartContainer">
            <h1 className="cartTitle">Your Cart</h1>
            {message && <div className="notification">{message}</div>}
            {items.length === 0 ? (
                <p className="emptyMessage">Your cart is empty</p>
            ) : (
                <ul className="cartList">
                    {items.map((item) => (
                        <li key={item.itemId} className="cartItem">
                            <img src={item.imageUrl} alt={item.name} className="productImage" />
                            <div className="productDetails">
                                <h2 className="productName">{item.name}</h2>
                                <p className="productPrice">Price: ${item.price}</p>
                                <p className="productQuantity">Quantity: {item.quantity}</p>
                            </div>
                            <button className="removeButton" onClick={() => removeItem(item.itemId)}>
                                Remove
                            </button>
                            <button className="incrementButton" onClick={() => updateQuantity(item.itemId, item.quantity + 1)}>
                                +
                            </button>
                            <button
                                className="decrementButton"
                                onClick={() => updateQuantity(item.itemId, item.quantity - 1)}
                                disabled={item.quantity <= 1}
                            >
                                -
                            </button>
                            <button className="wishlistButton" onClick={() => addToWishlist(item)}>
                                Add to Wishlist
                            </button>
                        </li>
                    ))}
                </ul>
            )}

            <div className="cart-summary">
                <h3>Cart Summary</h3>
                <table>
                    <tbody>
                    <tr>
                        <td>Total:</td>
                        <td>${calculateTotal().toFixed(2)}</td>
                    </tr>
                    </tbody>
                </table>
            </div>
            <button onClick={proceedToShipping} disabled={items.length === 0}>
                Proceed to Shipping
            </button>
        </div>
    );
};

export default CartPage;
