import React, { useState } from 'react';
import { useCart } from '../../context/CartContext'
import './CartPage.scss';

const CartPage: React.FC = () => {
    const { state, dispatch } = useCart();

    const handleQuantityChange = (sku: string, quantity: number) => {
        dispatch({ type: 'UPDATE_QUANTITY', sku, quantity });
    };

    const removeItem = (sku: string) => {
        dispatch({ type: 'REMOVE_FROM_CART', sku });
    };

    const subtotal = state.items.reduce((total, item) => total + item.price * item.quantity, 0);
    const shippingCost = 5.00; // Example flat rate
    const orderTotal = subtotal + shippingCost;

    return (
        <div className="cart-page">
            <h2>Shopping Cart</h2>
            {state.items.map(item => (
                <div key={item.sku} className="cart-item">
                    <p>{item.name}</p>
                    <input
                        type="number"
                        min="1"
                        value={item.quantity}
                        onChange={(e) => handleQuantityChange(item.sku, parseInt(e.target.value))}
                    />
                    <button onClick={() => removeItem(item.sku)}>Remove</button>
                    <p>${(item.price * item.quantity).toFixed(2)}</p>
                </div>
            ))}
            <div className="cart-summary">
                <p>Subtotal: ${subtotal.toFixed(2)}</p>
                <p>Shipping: ${shippingCost.toFixed(2)}</p>
                <h4>Order Total: ${orderTotal.toFixed(2)}</h4>
                <button>Proceed to Checkout</button>
            </div>
        </div>
    );
};

export default CartPage;
