import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useCart } from '../../context/CartContext';

const PaymentPage: React.FC = () => {
    const [paymentMethod, setPaymentMethod] = useState('checkmo'); // Domyślna metoda płatności to "checkmo"
    const { items, setPaymentMethodAndPlaceOrder, clearCart } = useCart();
    const location = useLocation();
    const navigate = useNavigate();

    const shippingData = location.state?.shippingData;

    const handlePlaceOrder = async () => {
        if (!shippingData || items.length === 0) {
            alert('Shipping details or cart items are missing');
            return;
        }

        const orderId = await setPaymentMethodAndPlaceOrder(paymentMethod);
        if (orderId) {
            alert(`Order placed successfully! Your order ID is ${orderId}`);
            clearCart();
            navigate('/confirmation', { state: { orderId } });
        } else {
            alert('Failed to place order. Please try again.');
        }
    };

    return (
        <div className="payment-form">
            <h3>Select Payment Method</h3>
            <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                <option value="checkmo">Check / Money Order</option>
                <option value="paypal">PayPal</option>
                <option value="banktransfer">Bank Transfer</option>
            </select>
            <button onClick={handlePlaceOrder}>Place Order</button>
        </div>
    );
};

export default PaymentPage;
