import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { useUserContext } from './UserContext';

interface CartItem {
    itemId: string;
    productId: string;
    name: string;
    quantity: number;
    price: number;
    imageUrl?: string;
}

interface ShippingAddress {
    firstName: string;
    lastName: string;
    street: string[];
    city: string;
    region: string;
    regionCode: string;
    postalCode: string;
    country: string;
    countryCode: string;
    telephone: string;
    saveInAddressBook?: boolean;
}

interface CartContextType {
    items: CartItem[];
    addItem: (sku: string, quantity: number) => Promise<void>;
    removeItem: (itemId: string) => Promise<void>;
    updateQuantity: (itemId: string, quantity: number) => Promise<void>;
    clearCart: () => void;
    fetchCart: () => Promise<void>;
    total: number;
    shippingAddress: ShippingAddress | null;
    shippingMethod: string | null;
    paymentMethod: string | null; // Dodaj paymentMethod do kontekstu
    setShippingAddress: (address: ShippingAddress) => Promise<void>;
    setShippingMethod: (methodCode: string) => Promise<void>;
    setPaymentMethod: (paymentCode: string) => Promise<void>;
    placeOrder: () => Promise<string>;
}

const CartContext = createContext<CartContextType | undefined>(undefined);

export const CartProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
    const { token, isLoggedIn } = useUserContext();
    const [items, setItems] = useState<CartItem[]>([]);
    const [cartId, setCartId] = useState<string | null>(null);
    const [total, setTotal] = useState<number>(0);
    const [shippingAddress, setShippingAddressState] = useState<ShippingAddress | null>(null);
    const [shippingMethod, setShippingMethodState] = useState<string | null>(null);
    const [paymentMethod, setPaymentMethodState] = useState<string | null>(null); // Dodajemy paymentMethod

    useEffect(() => {
        if (isLoggedIn) {
            fetchCart();
        } else {
            setItems([]);
            setCartId(null);
        }
    }, [isLoggedIn]);

    const fetchCart = async () => {
        if (!token) return;

        const query = `
            query {
                customerCart {
                    id
                    items {
                        id
                        product {
                            sku
                            name
                            thumbnail {
                                url
                            }
                        }
                        quantity
                        prices {
                            price {
                                value
                            }
                        }
                    }
                }
            }
        `;

        try {
            const response = await fetch('http://mint.local/graphql', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify({ query }),
            });

            const result = await response.json();
            const cartData = result.data.customerCart;

            setCartId(cartData.id);
            const cartItems = cartData.items.map((item: any) => ({
                itemId: item.id,
                productId: item.product.sku,
                name: item.product.name,
                quantity: item.quantity,
                price: item.prices.price.value,
                imageUrl: item.product.thumbnail.url, // Przypisujemy URL obrazu
            }));
            setItems(cartItems);
        } catch (error) {
            console.error("Error fetching cart:", error);
        }
    };

    const addItem = async (sku: string, quantity: number) => {
        if (!token || !cartId) return;

        const mutation = `
            mutation {
                addSimpleProductsToCart(
                    input: {
                        cart_id: "${cartId}"
                        cart_items: [
                            {
                                data: {
                                    quantity: ${quantity}
                                    sku: "${sku}"
                                }
                            }
                        ]
                    }
                ) {
                    cart {
                        items {
                            id
                            product {
                                sku
                                name
                                thumbnail {
                                    url
                                }
                            }
                            quantity
                            prices {
                                price {
                                    value
                                }
                            }
                        }
                    }
                }
            }
        `;

        try {
            const response = await fetch('http://mint.local/graphql', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify({ query: mutation }),
            });

            const result = await response.json();
            const cartItems = result.data.addSimpleProductsToCart.cart.items.map((item: any) => ({
                itemId: item.id,
                productId: item.product.sku,
                name: item.product.name,
                quantity: item.quantity,
                price: item.prices.price.value,
                imageUrl: item.product.thumbnail.url, // Przypisujemy URL obrazu
            }));
            setItems(cartItems);
        } catch (error) {
            console.error("Error adding item to cart:", error);
        }
    };



    const removeItem = async (itemId: string) => {
        if (!token) return;

        const mutation = `
      mutation {
        removeItemFromCart(
          input: {
            cart_id: "${cartId}",
            cart_item_id: "${itemId}"
          }
        ) {
          cart {
            items {
              id
            }
          }
        }
      }
    `;

        try {
            await fetch('http://mint.local/graphql', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify({ query: mutation }),
            });
            setItems((prevItems) => prevItems.filter((item) => item.itemId !== itemId));
        } catch (error) {
            console.error("Error removing item from cart:", error);
        }
    };

    const updateQuantity = async (itemId: string, quantity: number) => {
        if (!token || !cartId) return;

        // Prevent negative quantities
        if (quantity < 1) {
            await removeItem(itemId);
            return;
        }

        const mutation = `
      mutation {
        updateCartItems(
          input: {
            cart_id: "${cartId}",
            cart_items: [
              {
                cart_item_id: "${itemId}",
                quantity: ${quantity}
              }
            ]
          }
        ) {
          cart {
            items {
              id
              quantity
            }
          }
        }
      }
    `;

        try {
            const response = await fetch('http://mint.local/graphql', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify({ query: mutation }),
            });

            const result = await response.json();
            console.log("Response from updateQuantity:", result);

            const updatedItems = result.data.updateCartItems.cart.items.map((item: any) => ({
                itemId: item.id,
                quantity: item.quantity,
            }));

            setItems((prevItems) =>
                prevItems.map((item) => {
                    const updatedItem = updatedItems.find((uItem: any) => uItem.itemId === item.itemId);
                    return updatedItem ? { ...item, quantity: updatedItem.quantity } : item;
                })
            );
        } catch (error) {
            console.error("Error updating item quantity:", error);
        }
    };


    const clearCart = () => setItems([]);


const setShippingAddress = async (address: ShippingAddress) => {
    if (!cartId || !token) return;

    const mutation = `
        mutation {
            setShippingAddressesOnCart(
                input: {
                    cart_id: "${cartId}"
                    shipping_addresses: [
                        {
                            address: {
                                firstname: "${address.firstName}"
                                lastname: "${address.lastName}"
                                street: ${JSON.stringify(address.street)}
                                city: "${address.city}"
                                region: "${address.regionCode}"
                                postcode: "${address.postalCode}"
                                country_code: "${address.countryCode}"
                                telephone: "${address.telephone}"
                                save_in_address_book: ${address.saveInAddressBook || false}
                            },
                        }
                    ]
                }
            ) {
                cart {
                    shipping_addresses {
                        firstname
                        lastname
                        street
                        city
                        region {
                            code
                            label
                        }
                        postcode
                        telephone
                        country {
                            code
                            label
                        }
                    }
                }
            }
        }
    `;

    try {
        await fetch('http://mint.local/graphql', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ query: mutation }),
        });
        setShippingAddressState(address);
    } catch (error) {
        console.error("Error setting shipping address:", error);
    }
};

const setPaymentMethod = async (paymentCode: string) => {
    if (!cartId || !token) return;

    const mutation = `
            mutation {
                setPaymentMethodOnCart(
                    input: {
                        cart_id: "${cartId}"
                        payment_method: {
                            code: "${paymentCode}"
                        }
                    }
                ) {
                    cart {
                        selected_payment_method {
                            code
                        }
                    }
                }
            }
        `;

    try {
        await fetch('http://mint.local/graphql', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ query: mutation }),
        });
        setPaymentMethodState(paymentCode);  // Ustawienie paymentMethod
    } catch (error) {
        console.error("Error setting payment method:", error);
    }
};
    const setShippingMethod = async (shippingMethod: string) => {
        if (!cartId || !token) return;
        const carrierCode = "tablerate";  // Użyj domyślnego dostawcy
        const methodCode = shippingMethod === "express" ? "express" : "bestway";  // 'bestway' dla standardowej wysyłki
        const mutation = `
        mutation {
            setShippingMethodsOnCart(
                input: {
                    cart_id: "${cartId}"
                    shipping_methods: [
                        {
                            carrier_code: "${carrierCode}"
                            method_code: "${methodCode}"
                        }
                    ]
                }
            ) {
                cart {
                    shipping_addresses {
                        selected_shipping_method {
                            carrier_code
                            carrier_title
                            method_code
                            method_title
                            amount {
                                value
                                currency
                            }
                        }
                    }
                }
            }
        }
    `;

        try {
            const response = await fetch('http://mint.local/graphql', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify({ query: mutation }),
            });
            const result = await response.json();

            if (result.data.setShippingMethodsOnCart.cart.shipping_addresses.length > 0) {
                const shippingMethodData = result.data.setShippingMethodsOnCart.cart.shipping_addresses[0].selected_shipping_method;
                console.log("Selected shipping method:", shippingMethodData);
                setShippingMethodState(shippingMethodData.method_title); // Ustawienie stanu metody wysyłki
            }
        } catch (error) {
            console.error("Error setting shipping method:", error);
        }
    };
    const placeOrder = async () => {
        if (!cartId || !shippingMethod) return "";
        const mutation = `
           mutation {
            placeOrder(input: { cart_id: "${cartId}" }) {
                order {
                      order_id
                  }
            }
        }
        `;

        try {
            const response = await fetch('http://mint.local/graphql', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify({ query: mutation }),
            });

            const result = await response.json();
            if (result.data.placeOrder.errors.length === 0) {
                return result.data.placeOrder.order.order_id;
            } else {
                console.error("Order errors:", result.data.placeOrder.errors);
                return "";
            }
        } catch (error) {
            console.error("Error placing order:", error);
            return "";
        }
    };

    return (
        <CartContext.Provider value={{
            items,
            total,
            shippingAddress,
            shippingMethod,
            paymentMethod,
            addItem,
            removeItem,
            updateQuantity,
            clearCart,
            fetchCart,
            setShippingAddress,
            setShippingMethod,
            setPaymentMethod,
            placeOrder,
        }}>
            {children}
        </CartContext.Provider>
    );
};

export const useCart = () => {
    const context = useContext(CartContext);
    if (!context) {
        throw new Error("useCart must be used within a CartProvider");
    }
    return context;
};
