import React, { useState } from 'react';
import './Signup.scss';

const Signup: React.FC = () => {
    const [showPassword, setShowPassword] = useState(false);
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handlePasswordToggle = () => {
        setShowPassword(!showPassword);
    };

    const handleSignup = async (e: React.FormEvent) => {
        e.preventDefault();

        const query = `
            mutation {
                createCustomer(
                    input: {
                        email: "${email}",
                        firstname: "${firstName}",
                        lastname: "${lastName}",
                        password: "${password}"
                    }
                ) {
                    customer {
                        id
                        email
                        firstname
                        lastname
                    }
                }
            }
        `;

        try {
            const response = await fetch('http://mint.local/graphql', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ query }),
            });

            const result = await response.json();

            if (result.data.createCustomer.customer) {
                setSuccessMessage('Account created successfully!');
                setFirstName('');
                setLastName('');
                setEmail('');
                setPassword('');
            } else {
                const userErrors = result.data.createCustomer.userErrors;
                if (userErrors && userErrors.length > 0) {
                    setError(userErrors[0].message);
                } else {
                    setError('An unknown error occurred.');
                }
            }
        } catch (err) {
            setError('An error occurred while creating the account.');
        }
    };

    return (
        <div className="signup-container">
            <h1>Create New Customer Account</h1>
            <form className="signup-form" onSubmit={handleSignup}>
                <div className="form-group">
                    <label htmlFor="firstName">First Name <span className="required">*</span></label>
                    <input
                        type="text"
                        id="firstName"
                        placeholder=" "
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="lastName">Last Name <span className="required">*</span></label>
                    <input
                        type="text"
                        id="lastName"
                        placeholder=" "
                        value={lastName}
                        onChange={(e) => setLastName(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="email">Email <span className="required">*</span></label>
                    <input
                        type="email"
                        id="email"
                        placeholder=" "
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="password">Password <span className="required">*</span></label>
                    <div className="password-input-wrapper">
                        <input
                            type={showPassword ? 'text' : 'password'}
                            id="password"
                            placeholder=" "
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                        <span
                            className="toggle-password-visibility"
                            onClick={handlePasswordToggle}
                        >
                            <i className={`fas ${showPassword ? 'fa-eye-slash' : 'fa-eye'}`}></i>
                        </span>
                    </div>
                </div>

                <div className="form-group">
                    <label className="checkbox-option">
                        <input type="checkbox" />
                        Sign Up for Newsletter
                    </label>
                </div>

                <div className="form-group">
                    <label className="checkbox-option">
                        <input type="checkbox" />
                        Allow remote shopping assistance
                    </label>
                </div>

                {error && <p className="error">{error}</p>}
                {successMessage && <p className="success">{successMessage}</p>}

                <button type="submit" className="submit-button">
                    Create an Account
                </button>

                {/* Informacja o polach wymaganych */}
                <p className="required-info">Fields marked with <span className="required">*</span> are required.</p>
            </form>
        </div>
    );
};

export default Signup;
