import React, { useEffect, useState } from 'react';
import './TopSellers.scss';


interface Product {
    id: string;
    name: string;
    sku: string;
    price: {
        regularPrice: {
            amount: {
                value: number;
                currency: string;
            };
        };
    };
    description: {
        html: string;
    };
}

const TopSellers: React.FC = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        async function fetchTopSellers() {
            const query = `
            {
                products(search: "") {
                    items {
                        id
                        name
                        sku
                        price {
                            regularPrice {
                                amount {
                                    value
                                    currency
                                }
                            }
                        }
                        description {
                            html
                        }
                    }
                }
            }
            `;

            const url = `http://mint.local/graphql?query=${encodeURIComponent(query)}`;

            try {
                const response = await fetch(url, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                const result = await response.json();
                setProducts(result.data.products.items);
            } catch (error) {
                console.error('Error fetching top sellers:', error);
                setError('Failed to fetch top sellers. Please try again later.');
            }
        }

        fetchTopSellers();
    }, []);

    return (
        <div className="topSellersSection">
            <h2>Top Sellers</h2>
            {error && <p className="error">{error}</p>} {/* Wyświetlamy komunikat o błędzie, jeśli wystąpił */}
            <div className="topSellersContainer">
                {products.slice(0, 6).map(product => (
                    <div key={product.id} className="product">
                        <h3>{product.name}</h3>
                        <p className="productSku">SKU: {product.sku}</p>
                        <p>
                            {product.price.regularPrice.amount.value} {product.price.regularPrice.amount.currency}
                        </p>
                        <div
                            className="productDescription"
                            dangerouslySetInnerHTML={{ __html: product.description.html }}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
};

export default TopSellers;
